import { Component, Input } from '@angular/core';
import { Expense } from 'src/app/Expense';

@Component({
  selector: 'app-expense',
  templateUrl: './expense.component.html',
  styleUrls: ['./expense.component.css']
})
export class ExpenseComponent {
  @Input() expense!: Expense;
}
<div [ngClass]="'expense-edit'">
    <div [ngClass]="'expense-item'">
        <div>
            <h3>{{expense.category | titlecase }}</h3>
        </div>
        <div>
            <p [ngClass]="expense.type === 'expense'? 'exp': 'inc' ">{{expense.amount | currency:'INR':'symbol':'1.0-0'}}
            </p>
            <h5>{{expense.date | date:'mediumDate' }}</h5>
        </div>

    </div>
    <div [ngClass]="'btn'">
        <button [ngClass]="'delete'"> * </button>
        <button [ngClass]="'edit'"> Edit </button>
    </div>
</div>
