import { Component, EventEmitter, Input, Output, SimpleChanges } from '@angular/core';
import { Subscription } from 'rxjs';
import { Expense } from 'src/app/Expense';
import { ExpensesService } from 'src/app/services/expenses.service';
import { ModalService } from 'src/app/services/modal.service';

@Component({
  selector: 'app-expense',
  templateUrl: './expense.component.html',
  styleUrls: ['./expense.component.css'],
})
export class ExpenseComponent {
  @Output() onDelete: EventEmitter<Expense> = new EventEmitter();
  @Output() outputToParent = new EventEmitter<any>();
  @Input() expense!: Expense;
  @Input() showModal!: boolean;
  subscription!: Subscription;

  constructor(private modalService: ModalService) {}

  openModal() {
    this.outputToParent.emit(true);
  }
  // openModal(): void {    
  //   this.modalService.onOpenModal();
  //   // this.subscription= this.modalService.showModal.subscribe(
  //   //   (value: boolean) => this.showModal=value
  //   // )
  //   //console.log(this.showModal);
  // }

  addExpense(expense:Expense){
    console.log(expense);
  }

  onDeleteExp(exp: Expense){
    console.log(exp);
    // this.onDelete.emit(exp);
  }
}
<div [ngClass]="'expense-edit'">
    <div [ngClass]="'expense-item'">
        <div>
            <h3>{{expense.category | titlecase }}</h3>
        </div>
        <div>
            <p [ngClass]="expense.type === 'expense'? 'exp': 'inc' ">{{expense.amount | currency:'INR':'symbol':'1.0-0'}}
            </p>
            <h5>{{expense.date | date:'mediumDate' }}</h5>
        </div>

    </div>
    {{showModal}}
    <div [ngClass]="'btn'">
        <button [ngClass]="'delete'" (click)="onDeleteExp(expense)"> Del </button>
        <button [ngClass]="'edit'" (click)="openModal()"> Edit </button>
    </div>
</div>
