import { Component, EventEmitter, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Subscription } from 'rxjs';
import { Expense } from 'src/app/Expense';
import { ModalService } from 'src/app/services/modal.service';

@Component({
  selector: 'app-add-expense',
  templateUrl: './add-expense.component.html',
  styleUrls: ['./add-expense.component.css']
})
export class AddExpenseComponent {
  @Output() onAddExpense: EventEmitter<Expense> = new EventEmitter();
  expenseForm!: FormGroup;
  showAddExpense: boolean = false;
  subscription!:Subscription;
  constructor(private modalService: ModalService, private formBuilder: FormBuilder) {
    this.subscription=this.modalService.onToggle().subscribe(
      (value: boolean) => this.showAddExpense=value
    )
   }




  ngOnInit() {
    this.expenseForm = this.formBuilder.group({
      expenseType: ['', Validators.required],
      detail: ['', Validators.required],
      tags: [''],
      date: ['', Validators.required],
      time: [''],
      amount: ['', Validators.required]
    });
  }

  onSubmit() {
    console.log(this.expenseForm.value);
    //this.onAddExpense.emit(newExpense);
    // You can make a service call to post the form data to the backend
  }



