<div [ngClass]="'container'">
<div>
    <h1>Income</h1><h5>{{4000}}</h5>
    <h2>Expenses</h2><h5>{{455}}</h5>
</div>
<app-expense-list [expenses]="expenses"></app-expense-list>
</div>
<button [ngClass]="'add-button'" (onAddTask)="addTask($event)" >+</button>
