import { Component, EventEmitter, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Subscription } from 'rxjs';
import { Expense } from 'src/app/Expense';
import { ModalService } from 'src/app/services/modal.service';

@Component({
  selector: 'app-add-expense',
  templateUrl: './add-expense.component.html',
  styleUrls: ['./add-expense.component.css']
})
export class AddExpenseComponent {
  @Output() onAddExpense: EventEmitter<Expense> = new EventEmitter();
  expenseForm!: FormGroup;

  constructor(private modalService: ModalService, private formBuilder: FormBuilder) {

   } 

  ngOnInit() {
    this.expenseForm = this.formBuilder.group({
      expenseType: ['', Validators.required],
      category: ['', Validators.required],      
      date: ['', Validators.required],      
      amount: ['', Validators.required]
    });
  }

  onSubmit() {
    const value = this.expenseForm.value;
    const newExpense={
          category: value.category,
          //date:value.date,
          type:value.expenseType,
          amount:value.amount
        }
        console.log(newExpense);
    this.onAddExpense.emit(newExpense);
    // You can make a service call to post the form data to the backend
  }

  closeModal(): void {
    this.modalService.toggleModal();
  }

}
<div [ngClass]="'add-new'">
    <!-- <div class="load">
      <span class="loader">
        <span class="loader-inner"></span>
      </span>
    </div> -->
    <form [formGroup]="expenseForm" id="form1" (ngSubmit)="onSubmit()">
      <select formControlName="expenseType">
        <option value="">Select expense type</option>
        <option value="Expense">Expense</option>
        <option value="Income">Income</option>
      </select>
      <br>
      <br>
      <input type='text' formControlName="category" placeholder='Category of the Expense/Income' />
      <br>
      <!-- <br>
      <input type='date' formControlName="date" id="date1" value="" />
      <br> -->
      <br>
      <input type='number' formControlName="amount" placeholder='&#8377;Enter Amount' />
      <br>
      <br>
      <div [ngClass]="'flex'">
        <button type="submit" class="buttonFlat">
            ADD
          </button>
          <button class="buttonFlat" (click)="closeModal()">
            CANCEL
          </button>
      </div>

    </form>
</div>
