import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'expense-tracker';
  addTask(task:Task){
    this.Service.addTask(task).subscribe((task)=>this.tasks.push(task));
  }
}
