import { Component, HostBinding } from '@angular/core';

import { HttpClient } from '@angular/common/http';
import { Expense } from 'src/app/Expense';
import { Observable, Subscription } from 'rxjs';
import { ExpensesService } from 'src/app/services/expenses.service';
import { ModalService } from 'src/app/services/modal.service';


@Component({
  selector: 'app-monthly',
  templateUrl: './monthly.component.html',
  styleUrls: ['./monthly.component.css'],
})
export class MonthlyComponent {
  expenses: Expense[] = [];
  showModal: boolean = false;
  income:number = 0;
  expense:number = 0;
  subscription!:Subscription;
  editExpense!:Expense;
  constructor(private expensesService: ExpensesService,private modalService: ModalService) {}

  ngOnInit(): void {
    // Earlier
    this.expensesService.getExpenses().subscribe((data) => {
      console.log('success', data);
      this.expenses = data;

      this.expense = data
        .filter((datum) => datum.type === 'expense')
        .map((i) => i.amount)
        .reduce((a, b) => a + b);

      this.income = data
        .filter((datum) => datum.type === 'income')
        .map((i) => i.amount)
        .reduce((a, b) => a + b);

    });
  }

  openModal(): void {    
    this.modalService.onOpenModal();
  }

  deleteExpense(expense:Expense){
    this.expensesService.deleteExpense(expense).subscribe(()=> this.expenses = this.expenses.filter(e=>e.id !== expense.id));
  }

  addExpense(expense:Expense){
    this.expensesService.addExpense(expense).subscribe((expense)=>this.expenses.push(expense));
  }
}
